changeSet.sql:
UPDATE catalog_access_policy
   SET allow_roles = UPPER(allow_roles)
 WHERE allow_roles IS NOT NULL;

UPDATE catalog_access_policy
   SET allow_roles = regexp_replace(allow_roles, '(^|[,\s])DEPT_DATA_VIEWER([,\s]|$)', E'\\1ROLE_DEPT_DATA_VIEWER\\2', 'g')
 WHERE allow_roles ~ '(^|[,\s])DEPT_DATA_VIEWER([,\s]|$)';

